/*!50003 DROP PROCEDURE IF EXISTS  `InserGear` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`traxex`@`%` PROCEDURE `InserGear`()
BEGIN
    DECLARE i int DEFAULT 1;
    WHILE i <= 100 DO
        INSERT INTO tbl_gear(gameCode, gearNum) VALUES (1, i);
        SET i = i + 1;
    END WHILE;
    
    WHILE i <= 200 DO
        INSERT INTO tbl_gear(gameCode, gearNum) VALUES (2, i);
        SET i = i + 1;
    END WHILE;
    
    WHILE i <= 300 DO
        INSERT INTO tbl_gear(gameCode, gearNum) VALUES (3, i);
        SET i = i + 1;
    END WHILE;
    
    WHILE i <= 400 DO
        INSERT INTO tbl_gear(gameCode, gearNum) VALUES (4, i);
        SET i = i + 1;
    END WHILE;
END */$$
DELIMITER ;

